const payload = $input.first().json;

// Validare simplă
const errors = [];

if (!payload.inspeccionId) errors.push('inspeccionId is required');
if (!payload.empleado) errors.push('empleado is required');
if (!payload.vehicul) errors.push('vehicul is required');
if (!payload.locatie) errors.push('locatie is required');
if (!payload.items || !Array.isArray(payload.items)) errors.push('items array is required');
if (!payload.meta) errors.push('meta is required');

if (errors.length > 0) {
  return [{
    json: {
      success: false,
      error: 'Validation failed',
      details: errors
    }
  }];
}

return [{
  json: {
    success: true,
    data: payload
  }
}];

const data = $input.first().json;

if (!data.success) {
  return [data];
}

const payload = data.data;

// Procesează semnăturile
const processedPayload = {
  ...payload,
  empleado: {
    ...payload.empleado,
    semnaturaPng: payload.empleado.semnaturaPng || null
  },
  meta: {
    ...payload.meta,
    semnaturaInspector: payload.meta.semnaturaInspector || null
  }
};

return [{
  json: {
    success: true,
    processedData: processedPayload
  }
}];

const data = $input.first().json;

if (!data.success) {
  return [data];
}

const payload = data.processedData;

// Generează conținutul pentru PDF
const pdfContent = {
  title: `Inspección ${payload.meta.numeroInspeccion}`,
  date: new Date(payload.timestamp).toLocaleDateString('es-ES'),
  employee: {
    name: payload.empleado.nume,
    email: payload.empleado.email,
    signature: payload.empleado.semnaturaPng
  },
  vehicle: {
    plate: payload.vehicul.placa,
    km: payload.vehicul.km
  },
  location: {
    lat: payload.locatie.lat,
    lng: payload.locatie.lng
  },
  items: payload.items,
  inspector: payload.meta.inspector,
  inspectorSignature: payload.meta.semnaturaInspector,
  observations: payload.observatii || ''
};

return [{
  json: {
    success: true,
    pdfContent: pdfContent,
    originalData: payload
  }
}];

const response = $input.first();
const pdfContent = $('Generate PDF Content').first().json;

if (response.statusCode >= 200 && response.statusCode < 300) {
  return [{
    json: {
      success: true,
      pdfUrl: 'https://your-server.com/pdfs/inspection.pdf',
      inspectionId: pdfContent.originalData.inspeccionId,
      numeroInspeccion: pdfContent.pdfContent.title
    }
  }];
} else {
  return [{
    json: {
      success: false,
      error: 'PDF generation failed',
      statusCode: response.statusCode
    }
  }];
}
