// Validare date de inspecÈ›ie
const payload = $input.first().json;

console.log('ğŸ” Validare payload inspecÈ›ie:', JSON.stringify(payload, null, 2));

// Schema de validare
const requiredFields = {
  inspeccionId: 'string',
  timestamp: 'string',
  empleado: 'object',
  vehicul: 'object',
  locatie: 'object',
  observatii: 'string',
  items: 'array',
  meta: 'object'
};

const empleadoFields = {
  id: 'string',
  nume: 'string',
  email: 'string'
};

const vehiculFields = {
  placa: 'string',
  km: 'number'
};

const locatieFields = {
  lat: 'number',
  lng: 'number'
};

const metaFields = {
  versiuneSchema: 'number',
  clientApp: 'string',
  type: 'string',
  inspector: 'string',
  numeroInspeccion: 'string'
};

// FuncÈ›ie de validare
function validateField(value, fieldName, expectedType) {
  if (value === undefined || value === null) {
    return { valid: false, error: `${fieldName} is required` };
  }

  if (expectedType === 'string' && typeof value !== 'string') {
    return { valid: false, error: `${fieldName} must be a string` };
  }

  if (expectedType === 'number' && typeof value !== 'number') {
    return { valid: false, error: `${fieldName} must be a number` };
  }

  if (expectedType === 'object' && typeof value !== 'object') {
    return { valid: false, error: `${fieldName} must be an object` };
  }

  if (expectedType === 'array' && !Array.isArray(value)) {
    return { valid: false, error: `${fieldName} must be an array` };
  }

  return { valid: true };
}

// Validare cÃ¢mpuri obligatorii
const errors = [];

for (const [field, type] of Object.entries(requiredFields)) {
  const validation = validateField(payload[field], field, type);
  if (!validation.valid) {
    errors.push(validation.error);
  }
}

// Validare empleado
if (payload.empleado) {
  for (const [field, type] of Object.entries(empleadoFields)) {
    const validation = validateField(payload.empleado[field], `empleado.${field}`, type);
    if (!validation.valid) {
      errors.push(validation.error);
    }
  }
}

// Validare vehicul
if (payload.vehicul) {
  for (const [field, type] of Object.entries(vehiculFields)) {
    const validation = validateField(payload.vehicul[field], `vehicul.${field}`, type);
    if (!validation.valid) {
      errors.push(validation.error);
    }
  }
}

// Validare locatie
if (payload.locatie) {
  for (const [field, type] of Object.entries(locatieFields)) {
    const validation = validateField(payload.locatie[field], `locatie.${field}`, type);
    if (!validation.valid) {
      errors.push(validation.error);
    }
  }
}

// Validare meta
if (payload.meta) {
  for (const [field, type] of Object.entries(metaFields)) {
    const validation = validateField(payload.meta[field], `meta.${field}`, type);
    if (!validation.valid) {
      errors.push(validation.error);
    }
  }
}

// Validare items
if (payload.items && Array.isArray(payload.items)) {
  if (payload.items.length === 0) {
    errors.push('items array cannot be empty');
  } else {
    payload.items.forEach((item, index) => {
      if (!item.cod || !item.descriere) {
        errors.push(`item[${index}] missing required fields: cod, descriere`);
      }
      if (typeof item.ok !== 'boolean') {
        errors.push(`item[${index}].ok must be a boolean`);
      }
      if (typeof item.nota !== 'number' || item.nota < 1 || item.nota > 5) {
        errors.push(`item[${index}].nota must be a number between 1-5`);
      }
    });
  }
}

// Validare semnÄƒturi (opÈ›ionale)
if (payload.empleado.semnaturaPng && !payload.empleado.semnaturaPng.startsWith('data:image/png;base64,')) {
  errors.push('empleado.semnaturaPng must be a valid PNG base64 data URL');
}

if (payload.meta.semnaturaInspector && !payload.meta.semnaturaInspector.startsWith('data:image/png;base64,')) {
  errors.push('meta.semnaturaInspector must be a valid PNG base64 data URL');
}

// VerificÄƒ dacÄƒ sunt erori
if (errors.length > 0) {
  console.log('âŒ Validare eÈ™uatÄƒ:', errors);
  return [{
    json: {
      success: false,
      error: 'Validation failed',
      details: errors,
      timestamp: new Date().toISOString()
    }
  }];
}

console.log('âœ… Validare reuÈ™itÄƒ');

// ReturneazÄƒ datele validate
return [{
  json: {
    success: true,
    validatedData: payload,
    timestamp: new Date().toISOString()
  }
}];

// Procesare semnÄƒturi
const validatedData = $input.first().json;

if (!validatedData.success) {
  return [validatedData]; // PropagÄƒ eroarea de validare
}

const payload = validatedData.validatedData;

console.log('ğŸ” Procesare semnÄƒturi...');

// FuncÈ›ie pentru validarea semnÄƒturilor
function validateSignature(signature, fieldName) {
  if (!signature) {
    return { valid: true, processed: null }; // SemnÄƒtura este opÈ›ionalÄƒ
  }

  if (!signature.startsWith('data:image/png;base64,')) {
    return { valid: false, error: `${fieldName} must be a valid PNG base64 data URL` };
  }

  // VerificÄƒ cÄƒ base64-ul este valid
  try {
    const base64Data = signature.replace('data:image/png;base64,', '');
    const decoded = atob(base64Data);

    if (decoded.length < 100) {
      return { valid: false, error: `${fieldName} appears to be too small for a valid signature` };
    }

    return { valid: true, processed: signature };
  } catch (error) {
    return { valid: false, error: `${fieldName} contains invalid base64 data` };
  }
}

// ProceseazÄƒ semnÄƒturile
const signatureErrors = [];

// SemnÄƒtura angajatului
const empleadoSignature = validateSignature(payload.empleado.semnaturaPng, 'empleado.semnaturaPng');
if (!empleadoSignature.valid) {
  signatureErrors.push(empleadoSignature.error);
}

// SemnÄƒtura inspectorului
const inspectorSignature = validateSignature(payload.meta.semnaturaInspector, 'meta.semnaturaInspector');
if (!inspectorSignature.valid) {
  signatureErrors.push(inspectorSignature.error);
}

// VerificÄƒ erorile de semnÄƒturÄƒ
if (signatureErrors.length > 0) {
  console.log('âŒ Erori semnÄƒturi:', signatureErrors);
  return [{
    json: {
      success: false,
      error: 'Signature validation failed',
      details: signatureErrors,
      timestamp: new Date().toISOString()
    }
  }];
}

// ActualizeazÄƒ payload-ul cu semnÄƒturile procesate
const processedPayload = {
  ...payload,
  empleado: {
    ...payload.empleado,
    semnaturaPng: empleadoSignature.processed
  },
  meta: {
    ...payload.meta,
    semnaturaInspector: inspectorSignature.processed
  }
};

console.log('âœ… SemnÄƒturi procesate cu succes');

return [{
  json: {
    success: true,
    processedData: processedPayload,
    timestamp: new Date().toISOString()
  }
}];

// Generare conÈ›inut PDF
const processedData = $input.first().json;

if (!processedData.success) {
  return [processedData]; // PropagÄƒ eroarea
}

const payload = processedData.processedData;

console.log('ğŸ” Generare conÈ›inut PDF...');

// GenereazÄƒ conÈ›inutul PDF-ului
const pdfContent = {
  // Header
  title: `InspecciÃ³n ${payload.meta.numeroInspeccion}`,
  date: new Date(payload.timestamp).toLocaleDateString('es-ES'),
  time: new Date(payload.timestamp).toLocaleTimeString('es-ES'),

  // InformaÈ›ii de bazÄƒ
  inspectionInfo: {
    id: payload.inspeccionId,
    type: payload.meta.type,
    inspector: payload.meta.inspector,
    supervisor: payload.meta.supervisor || 'N/A'
  },

  // Angajat
  employee: {
    id: payload.empleado.id,
    name: payload.empleado.nume,
    email: payload.empleado.email,
    signature: payload.empleado.semnaturaPng
  },

  // Vehicul
  vehicle: {
    plate: payload.vehicul.placa,
    km: payload.vehicul.km
  },

  // LocaÈ›ie
  location: {
    lat: payload.locatie.lat,
    lng: payload.locatie.lng,
    address: payload.observatii || 'N/A'
  },

  // Puncte de inspecÈ›ie
  items: payload.items.map(item => ({
    code: item.cod,
    description: item.descriere,
    ok: item.ok,
    score: item.nota,
    comment: item.comentariu || ''
  })),

  // SemnÄƒtura inspectorului
  inspectorSignature: payload.meta.semnaturaInspector,

  // ObservaÈ›ii generale
  observations: payload.observatii || 'Sin observaciones'
};

// CalculeazÄƒ statistici
const totalItems = pdfContent.items.length;
const passedItems = pdfContent.items.filter(item => item.ok).length;
const averageScore = pdfContent.items.reduce((sum, item) => sum + item.score, 0) / totalItems;

pdfContent.statistics = {
  totalItems,
  passedItems,
  failedItems: totalItems - passedItems,
  averageScore: Math.round(averageScore * 100) / 100,
  passRate: Math.round((passedItems / totalItems) * 100)
};

console.log('âœ… ConÈ›inut PDF generat:', {
  title: pdfContent.title,
  items: pdfContent.items.length,
  statistics: pdfContent.statistics
});

return [{
  json: {
    success: true,
    pdfContent: pdfContent,
    originalData: payload,
    timestamp: new Date().toISOString()
  }
}];

// Procesare rÄƒspuns PDF
const response = $input.first();
const pdfContent = $('Generate PDF Content').first().json;

console.log('ğŸ” Procesare rÄƒspuns PDF...');
console.log('Status:', response.statusCode);
console.log('Headers:', response.headers);

// VerificÄƒ dacÄƒ rÄƒspunsul este de succes
if (response.statusCode >= 200 && response.statusCode < 300) {
  // VerificÄƒ dacÄƒ rÄƒspunsul conÈ›ine PDF
  const contentType = response.headers['content-type'] || response.headers['Content-Type'] || '';

  if (contentType.includes('application/pdf')) {
    console.log('âœ… PDF generat cu succes');

    return [{
      json: {
        success: true,
        pdfData: response.body,
        contentType: 'application/pdf',
        inspectionId: pdfContent.originalData.inspeccionId,
        numeroInspeccion: pdfContent.pdfContent.title,
        timestamp: new Date().toISOString()
      }
    }];
  } else {
    // RÄƒspuns JSON cu informaÈ›ii despre PDF
    try {
      const responseData = typeof response.body === 'string' ? JSON.parse(response.body) : response.body;

      return [{
        json: {
          success: true,
          pdfUrl: responseData.pdfUrl || responseData.url,
          pdfId: responseData.id || responseData.pdfId,
          inspectionId: pdfContent.originalData.inspeccionId,
          numeroInspeccion: pdfContent.pdfContent.title,
          timestamp: new Date().toISOString()
        }
      }];
    } catch (error) {
      console.error('âŒ Eroare parsare rÄƒspuns JSON:', error);
      return [{
        json: {
          success: false,
          error: 'Invalid response format from PDF generator',
          details: 'Expected PDF or JSON response',
          timestamp: new Date().toISOString()
        }
      }];
    }
  }
} else {
  // Eroare de la serverul PDF
  console.error('âŒ Eroare server PDF:', response.statusCode, response.body);

  let errorMessage = 'PDF generation failed';
  let errorDetails = `HTTP ${response.statusCode}`;

  try {
    const errorData = typeof response.body === 'string' ? JSON.parse(response.body) : response.body;
    errorMessage = errorData.error || errorData.message || errorMessage;
    errorDetails = errorData.details || errorDetails;
  } catch (e) {
    // Nu se poate parsa rÄƒspunsul de eroare
  }

  return [{
    json: {
      success: false,
      error: errorMessage,
      details: errorDetails,
      statusCode: response.statusCode,
      timestamp: new Date().toISOString()
    }
  }];
}
